export default {
    name: "Register",
    data() {

        var name = (rule, value, callback) => {
            if (value === '') {
                callback(new Error('请输入账号'));
            } else if (value.length<3) {
                callback(new Error('至少位三个字符'));
            } else {
                callback();
            }
        };
        var pass = (rule, value, callback) => {
            if (value === '') {
                callback(new Error('请输入密码'));
            } else if (value.length<6) {
                callback(new Error('至少位六个字符'));
            } else {
                callback();
            }
        };
        var repass = (rule, value, callback) => {
            if (value === '') {
                callback(new Error('请输入密码'));
            } else if (value !== this.ruleForm.pass) {
                callback(new Error('两次输入密码不一致!'));
            } else {
                callback();
            }
        };
var phone= (rule, value, callback) => {
  if (value === '') {
    callback(new Error('请输入手机号'));
  } else if (value.length!==11) {
    callback(new Error('请输入正确的手机号'));
  } else {
    callback();
  }
};

      var yzm= (rule, value, callback) => {
        if (value === '') {
          callback(new Error('请输入验证码'));
        } else if (value.length!==6) {
          callback(new Error('请输入6位数字验证码'));
        } else {
          callback();
        }
      };
        return {
          loading:false,
            ruleForm: {
                name: '',
                pass:'',
                repass:'',
                yzm:'',
                phone:'',
              loading_text:''

            },
            rules: {
                name: [
                    { validator: name, trigger: 'blur' },

                ],
                pass: [
                    { validator: pass, trigger: 'blur' },
                ],
                repass:[
                    { validator: repass, trigger: 'blur' },
                ],
              phone: [
                { validator: phone, trigger: 'blur' }
                ],
              // yzm:[ { validator: yzm, trigger: 'blur' }]

            }
        }
    }
    ,
    methods: {
        login() {
            this.$router.push({path:'/'})
        },
        register(ruleForm){

            this.$refs[ruleForm].validate((valid) => {

                    if (valid) {
                      this.loading=true
                      this.loading_text='正在注册，请稍后'
                         this.$http.post(this.$HOST+"v1/register", this.$qs.stringify({
                            "username": this.ruleForm.name,
                            "password": this.ruleForm.pass,
                            "phone":this.ruleForm.phone,
                            "code":this.ruleForm.yzm,
                            "sign":this.$sign
                        })).then(res => {
                         if(res.data.code==0){
                           this.$alert('注册成功', '提示', {
                             confirmButtonText: '确定',
                             callback: action => {
                               this.$router.push({path:'/'})
                             }

                           })

                         }
                    else if(res.data.code==1){
                      this.loading=false
                             this.$refs[ruleForm].resetFields();
                           this.$alert('注册失败', '提示', {
                             confirmButtonText: '确定',
                           })
                         }
                         else if(res.data.code==2){
                           this.loading=false
                             this.$refs[ruleForm].resetFields();
                           this.$alert('用户名已存在', '提示', {
                             confirmButtonText: '确定',
                           })
                         }
                        })
                    }
                    else if(res.data.code==2){
                      this.loading=false
                      this.$refs[ruleForm].resetFields();
                      this.$alert('验证码错误', '提示', {
                        confirmButtonText: '确定',
                      })
                    }

                  else {
                        console.log('error submit!!');
                        return false;
                    }
        })
        },
        home(){
            this.$router.push({path:'/'})
        },
   resetForm(ruleForm){
    this.$refs[ruleForm].resetFields();
},
      getcode(ruleForm) {
        this.$refs[ruleForm].validate((valid) => {
          if (valid) {
            this.loading=true
            this.loading_text='正在发送验证码'
            if (this.ruleForm.phone.length !== 11) {
              this.$alert('请输入正确的手机格式', '提示', {
                confirmButtonText: '确定',
              })
            } else {
              this.$http.post(this.$HOST + 'v1/yzm', this.$qs.stringify({
                "phone": this.ruleForm.phone,
                "sign": this.$sign
              })).then(res => {
                if (res.data.code == 0) {
                  this.loading=false
                  this.$alert('发送成功', '提示', {
                    confirmButtonText: '确定',
                  })
                }
              })
            }
          }
          else{
            return
          }
        })


      }}
}
